$(document).ready(function(){
	$("[class^=flash_notice]").each(function(){
		if ($(this).html() != ''){
			alertify.dismissAll();
			if ($(this).data('type') == "notice") {
				alertify.success($(this).text());
			}
			else{
				alertify.error($(this).text());
			}
		}
	});
});
